#include <iostream>#include <SFML/Graphics.hpp>namespace Textures {    enum ID {Landscape, Ship, Ufo};}template <typename Resource, typename Identifier>class ResourceHolder {public:    Resource& get(Identifier id);    const Resource& get(Identifier id) const;    void load(Identifier id, const std::string& filename);    template <typename Parameter>    void load(Identifier id, const std::string& filename, const Parameter& secondParam);private:    void insertResource(Identifier id, std::unique_ptr<Resource> resource);    std::map<Identifier, std::unique_ptr<Resource>> mResourceMap;};#include "ResourceHolder.inl"